// No seu arquivo src/pages/Menu.js

import React from "react";
import "../style/menu.css";
import { Database, Wrench, BarChart3 } from "lucide-react";
import logo from "../img/logo.png";
import { useNavigate } from "react-router-dom";

export default function Menu() {
    const navigate = useNavigate();

    // Pega os dados do usuário do localStorage
    const usuarioLogado = JSON.parse(localStorage.getItem('usuarioLogado'));

    // Verifica se o setor do usuário é 'Administração' (ou o nome que você usa no seu banco)
    const podeVerGerenciamento = usuarioLogado?.setor === 'admin';

  return (
    <div className="pagina__menu">

      <header className="menu__logo">
        <img src={logo} alt="Transvicon Logística" />
      </header>

      {/* Exibe uma mensagem de boas-vindas */}
      {usuarioLogado && (
        <h2 style={{color: 'black', marginBottom: '2rem'}}>
          Bem-vindo, {usuarioLogado.nome}! (Setor: {usuarioLogado.setor})
        </h2>
      )}

      <div className="menu__opcoes">

          {/* O botão de Gerenciamento só aparece se a condição for verdadeira */}
          {podeVerGerenciamento && (
            <div className="menu__item" onClick={() => navigate("/gerenciamento")}>
             <Database size={60} color="#e60000" />
              <p>GERENCIAMENTO</p>
            </div>
          )}

        <div className="menu__item">
          <Wrench size={60} color="#e60000" />
          <p>ORDENS DE SERVIÇO</p>
        </div>

        <div className="menu__item">
          <BarChart3 size={60} color="#e60000" />
          <p>RELATÓRIOS</p>
        </div>

      </div>
      </div>
  );
}
