import React from "react";
import "../style/menu.css";
import { Database, Wrench, BarChart3 } from "lucide-react";
import logo from "../img/logo.png";
import { useNavigate } from "react-router-dom";

export default function Menu() {
    const navigate = useNavigate();

    // Pega os dados do usuário do localStorage
    const usuarioLogado = JSON.parse(localStorage.getItem('usuarioLogado'));

    // Verifica a permissão para ver o Gerenciamento
    const podeVerGerenciamento = usuarioLogado?.setor === 'ADMIN' || usuarioLogado?.setor === 'GESTOR';

    // --- FUNÇÃO DE LOGOUT ---
    const handleLogout = () => {
      // 1. Remove os dados do usuário do localStorage
      localStorage.removeItem('usuarioLogado');
      // 2. Redireciona para a página de login
      navigate('/');
    };

  return (
    <div className="pagina__menu">

      {/* O cabeçalho agora engloba o logo e as informações do usuário */}
      <header className="menu__header">
        <img src={logo} alt="Transvicon Logística" className="menu__logo_img" style={{maxWidth: '800px'}}/>

        {/* Mostra as informações do usuário e o botão de logout se estiver logado */}
        {usuarioLogado && (
          <div className="menu__usuario_info">
            <h2>
              Bem-vindo, {usuarioLogado.nome}!
            </h2>
            <button className="botao__logout" onClick={handleLogout}>
              SAIR
            </button>
          </div>
        )}
      </header>

      <div className="menu__opcoes">

          {/* O botão de Gerenciamento só aparece se a permissão for concedida */}
          {podeVerGerenciamento && (
            <div className="menu__item" onClick={() => navigate("/gerenciamento")}>
             <Database size={60} color="#e60000" />
              <p>GERENCIAMENTO</p>
            </div>
          )}

        <div className="menu__item">
          <Wrench size={60} color="#e60000" />
          <p>ORDENS DE SERVIÇO</p>
        </div>

        <div className="menu__item">
          <BarChart3 size={60} color="#e60000" />
          <p>RELATÓRIOS</p>
        </div>
      </div>
    </div>
  );
}
